mapper.select:
SELECT COUNT(*)
FROM VW_SYX_ZLGL_CPWCL t
WHERE t.FID = #{fid}

SELECT COUNT(*)
FROM VW_SYX_ZLGL_CPWCL t1
WHERE t1.FID = #{fid}
  AND (
    t1.EACHFINISH = 0 AND t1.SELFFINISH = 0
    )

SELECT COUNT(*)
FROM VW_SYX_ZLGL_CPWCL t1
WHERE t1.FID = #{fid}
  AND (
    t1.EACHFINISH = t1.EACH AND t1.SELFFINISH = t1.SELF
    )
