mapper.select:
SELECT *
FROM t_sheet_data
WHERE stage_rel_id = #{item.stageRelId}
  AND sheet_id = #{item.sheetId}
  AND column_id = #{item.columnId}
  AND row_index = #{item.rowIndex} LIMIT 1

SELECT *
FROM t_sheet_data
WHERE 1=1

    AND stage_rel_id = #{item.stageRelId}


    AND sheet_id = #{item.sheetId}


    AND column_id = #{item.columnId}


    AND row_index = #{item.rowIndex}

ORDER BY row_index ASC

mapper.update:
UPDATE t_sheet_data
SET column_value = #{item.columnValue},
    updated_at   = NOW()
WHERE stage_rel_id = #{item.stageRelId}
  AND sheet_id = #{item.sheetId}
  AND column_id = #{item.columnId}
  AND row_index = #{item.rowIndex}
