mapper.select:
SELECT cpt.CFMANUFACTUREORDER AS "manufactureOrder", cpt.CFTASKID AS "taskId", cpt.CFOPERATIONNAME AS "operationName", cpt.CFSTATUS AS "status", cpt.CFOPERATIONID AS "operationId", cpt.CFOPERATIONNO AS "operationNo", cptask.CFWORKPERSONID AS "workPersonId", tbp.FNAME_L2 AS "workPersonName", cpt.CFOLDBEGINTIME AS "startTime", cpt.CFOLDENDTIME AS "endTime", cpt.CFSEQ AS "seq" FROM CT_PRE_TASKBMO cpt LEFT JOIN CT_PRJ_TASK cptask ON cptask.FID = cpt.CFTASKID LEFT JOIN T_BD_PERSON tbp ON tbp.FID = cptask.cfworkpersonid WHERE cpt.CFOPERATIONID is not null
AND cpt.CFOPERATIONNAME = #{item.operationName,jdbcType=VARCHAR}
AND cpt.CFManufactureOrder LIKE CONCAT('%',CONCAT(#{item.manufactureOrder},'%'))
AND cpt.CFOPERATIONID = #{item.operationId,jdbcType=VARCHAR}
AND cpt.CFOPERATIONNO = #{item.operationNo,jdbcType=VARCHAR}
ORDER BY cpt.CFSEQ
