mapper.select:
SELECT cifs.id AS columnId, cifs.column_name AS columnName, cifd.id AS id, cifd.check_item_id AS checkItemId, cifs.seq AS seq, cifd.row_index AS rowIndex, cifd.column_value AS columnValue FROM check_item_form_setting AS cifs LEFT JOIN check_item_form_data AS cifd ON cifd.column_id = cifs.id
AND cifd.row_index = #{item.rowIndex}
WHERE cifd.check_item_id = #{item.checkItemId}

mapper.update:
UPDATE check_item_form_data

    column_value = #{item.columnValue}

WHERE check_item_id = #{item.checkItemId}
AND row_index = #{item.rowIndex}
AND column_id = #{item.columnId}
