mapper.select:
SELECT *
FROM t_checkitem_tag
WHERE 1=1

    AND tag_name = #{item.tagName}


    AND tag_type = #{item.tagType}

ORDER BY created_at DESC

SELECT count(1)
FROM t_checkitem_tag_rel
WHERE checkitem_tag_id = #{item.id}

SELECT *
FROM t_checkitem_tag
WHERE tag_type = #{tagType}
  AND tag_name = #{tagName}

mapper.delete:
DELETE
FROM t_checkitem_tag_rel
WHERE checkitem_id = #{checkItemId}
  AND checkitem_tag_id = #{tagId}

mapper.select:
SELECT COUNT(1)
FROM t_checkitem_tag_rel
WHERE checkitem_id = #{checkItemId}
  AND checkitem_tag_id = #{tagId}
